mapper.select:
SELECT
MEMBER_NO, MEMBER_ID, MEMBER_PW, MEMBER_EMAIL, MEMBER_NICKNAME, MEMBER_DEL_FL, MEMBER_TEL, MEMBER_HOME_TEL, MEMBER_ADRESS, PROFILE_IMG, PERSONAL_CODE, BACKGROUND_COLOR, AUTHORITY, INTRODUCTION
FROM "MEMBER" WHERE MEMBER_NO = #{memberNo}

SELECT ALERT_CONTENT FROM "ALERT"
JOIN "MEMBER" USING (MEMBER_NO)
WHERE MEMBER_NO = ${memberNo}
